package com.controlflow;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 100;
		
		// while (condition){
		
		while(i <8) {
			System.out.println(i);
			i++;
			
		}
		
		do {
			System.out.println(i);
			i++;
		}while(i<10);
		
	}

}
